var templates={nflPink:'<!-- .section-header-->     <div class="cdo-slick" > 	  	{{#each items}}         <div class="cdo-list-item" id="item-{{id}}">           <div class="cdo-list-item__image">             <a target="_blank" href="{{url}}"><img src="{{imgMedium}}" alt="{{{title}}}"></a>           </div>           <div class="cdo-list-item__body">             <h1 class="cdo-list-item__title" style="background-color:{{{brandColor}}}"><a target="_blank" href="{{url}}">{{{title}}}</a></h1>             <ul class="cdo-list-item__details">               <li class="cdo-closes" data-time-left="{{timeLeft}}">Closes in <strong>{{daysLeft}}d, {{hoursLeft}}h, {{minutesLeft}}m</strong></li>               <li class="cdo-current-bid">{{currentBidTitle}}: <strong class="cdo-current-bid-value">${{currentBid}}</strong></li> 	            <!--<li class="cdo-num-bids"><span class="cdo-bid-count-value">BIDS: {{bidCount}}</span></li>-->             </ul>             <div class="cdo-action"><a class="cdo-btn cdo-btn--action cdo-bid-link" target="_blank" href="{{url}}">{{actionTitle}}</a></div>           </div>         </div>       {{/each}}     </div>'};